import React from "react";
import FileUploadProgress from 'react-fileupload-progress';
import {
  MDBInput,
  MDBInputSelect,
  MDBFormInline,
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol
} from "mdbreact";
import DocsLink from "./DocsLink";

class InputPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "John Doe"
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    alert("MDBInput value: " + this.state.value);
    event.preventDefault();
  }

  saveToState = value => {
    this.setState({ ...this.state, value: value });
  };

  handleChange = value => {
    console.log(value);
  };

  render() {
    return (
      <MDBContainer className="mt-5">
        <DocsLink
          title="Inputs"
          href="https://mdbootstrap.com/docs/react/forms/inputs/"
        />
        <MDBContainer style={{ textAlign: "initial" }}>
          <div>

            <h4 className="mt-4">
              <strong>Horizontal form</strong>
            </h4>
            <form>
              <div className="form-group row">
                <label
                  htmlFor="inputEmail3"
                  className="col-sm-2 col-form-label"
                >
                  Email
                </label>
                <div className="col-sm-10">
                  <input
                    type="email"
                    className="form-control"
                    id="inputEmail3"
                    placeholder="Email"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="inputPassword3"
                  className="col-sm-2 col-form-label"
                >
                  Password
                </label>
                <div className="col-sm-10">
                  <input
                    type="password"
                    className="form-control"
                    id="inputPassword3"
                    placeholder="Password"
                  />
                </div>
              </div>
              <FileUploadProgress key='ex1' url='http://localhost:3000/api/upload'
                onProgress={(e, request, progress) => { console.log('progress', e, request, progress); }}
                onLoad={(e, request) => { console.log('load', e, request); }}
                onError={(e, request) => { console.log('error', e, request); }}
                onAbort={(e, request) => { console.log('abort', e, request); }}
              />
              <div className="form-group row">
                <div className="col-sm-10">
                  <button type="submit" className="btn btn-primary btn-md">
                    Sign in
                  </button>
                </div>
              </div>
            </form>

          </div>
        </MDBContainer>
      </MDBContainer>
    );
  }
}

export default InputPage;
